import React, { useState } from "react";
import { useDispatch } from "react-redux";
import deleteIcon from "../assets/image/delete.svg";
import { actionType } from "../store/actionType";
const { DECREMENT, DELETE, INCREMENT } = actionType;

export default function Match({ match }) {
  const [incrementValue, setIncrementValue] = useState(null);
  const [decrementValue, setDecrementValue] = useState(null);
  const dispatch = useDispatch();

  const incrementHandler =()=>{
    
    dispatch({ type: INCREMENT, payload: { id: match.id, value: pars(incrementValue) } });
  }

  return (
    <div class="match" id="singleMatch">
      <div class="wrapper">
        <button class="lws-delete">
          <img src={deleteIcon} alt="" />
        </button>
        <h3 class="lws-matchName">Match {match.name} </h3>
      </div>
      <div class="inc-dec">
        <form class="incrementForm" onClick={incrementHandler}>
          <h4>Increment</h4>
          <input
            type="number"
            name="increment"
            class="lws-increment"
            value={incrementValue}
            onChange={(e) => {
              setIncrementValue(e.target.value);
            }}
          />
        </form>
        <form class="decrementForm">
          <h4>Decrement</h4>
          <input type="number" name="decrement" class="lws-decrement" />
        </form>
      </div>
      <div class="numbers">
        <h2 class="lws-singleResult" id="score">
          {match.score}
        </h2>
      </div>
    </div>
  );
}
